krpano.action:
		set(layer[floormap_departure].bgcolor,get(layer[floormap_departure].bgcolor_passive));


		set(layer[floormap_arrival].bgcolor,get(layer[floormap_arrival].bgcolor_passive)); 
		set(layer[floormap_arrival].css,"text-align:center;font-size:calc((2vh + 2vw)/2);color:black");
		set(layer[floormap_departure].css,"text-align:center;font-size:calc((2vh + 2vw)/2);color:black");
if(get(name)=="floormap_departure",
	set(layer[floormap_departure].bgcolor,get(layer[floormap_departure].bgcolor_active));
	set(layer[floormap_departure].css,"text-align:center;font-size:calc((2vh + 2vw)/2); color:white");
	set(selected_floormap, "floormap_departure");
	,
	set(layer[floormap_arrival].bgcolor,get(layer[floormap_arrival].bgcolor_active));
	set(layer[floormap_arrival].css,"text-align:center;font-size:calc((2vh + 2vw)/2); color:white");
	set(selected_floormap, "floormap_arrival");
); 
change_floormap();

set(currentActiveScene,get(xml.scene));
if(get(selected_floormap)=="floormap_arrival" AND get(selected_terminal)=="floormap_terminal1",

	set(layer[textFloormapTitle].text,"Terminal 1 Arrival Floormap");
	if(get(currentActiveScene) == "scene_Terminal_1_Arrival_Exit_Area",

		,
		loadscene("scene_Terminal_1_Arrival_Exit_Area", null,MERGE,BLEND(1)); 

	);
	,

	if(get(selected_floormap)=="floormap_departure" AND get(selected_terminal)=="floormap_terminal1",
		set(layer[textFloormapTitle].text,"Terminal 1 Departure Floormap");

	    if(get(currentActiveScene) ==  "scene_terminal_1_departure_standee_logo",  
			,
			loadscene("scene_terminal_1_departure_standee_logo", null,MERGE,BLEND(1));
		);
	);
);
if(get(selected_floormap)=="floormap_arrival" AND get(selected_terminal)=="floormap_terminal2",

	set(layer[textFloormapTitle].text,"Terminal 2 Arrival Floormap");
	if(get(currentActiveScene) == "scene_Terminal_2_Arrival_Middle_Area",   
		,
		loadscene("scene_Terminal_2_Arrival_Middle_Area", null,MERGE,BLEND(1));
	);
,
	if(get(selected_floormap)=="floormap_departure" AND get(selected_terminal)=="floormap_terminal2",

		set(layer[textFloormapTitle].text,"Terminal 2 Departure Floormap");
	    if(get(currentActiveScene) == "scene_Terminal_2_Departure_Entrance_Middle_Area",    
		,
			loadscene("scene_Terminal_2_Departure_Entrance_Middle_Area", null,MERGE,BLEND(1));
		); 
	);
);
